CREATE TABLE IF NOT EXISTS `pays` (
  `id` int(11) NOT NULL AUTO_INCREMENT COMMENT '곡 아이디 (기본키)',
  `userId` varchar(20) NOT NULL COMMENT '곡 이름',
  `amountId` int(11) NOT NULL COMMENT '곡 이름',
  `expirationDate` datetime NOT NULL COMMENT '월정액 만료일',
  PRIMARY KEY (`id`),
  KEY `userId` (`userId`),
  KEY `amountId` (`amountId`),
  CONSTRAINT `pays_ibfk_1` FOREIGN KEY (`userId`) REFERENCES `users` (`id`) ON DELETE CASCADE ON UPDATE CASCADE,
  CONSTRAINT `pays_ibfk_2` FOREIGN KEY (`amountId`) REFERENCES `amounts` (`id`) ON DELETE NO ACTION ON UPDATE CASCADE
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

