CREATE TABLE IF NOT EXISTS `playhistorys` (
  `id` int(11) NOT NULL AUTO_INCREMENT COMMENT '플리 번호 (기본키)',
  `userId` varchar(20) NOT NULL COMMENT '플리에 넣은 회원의 id(user테이블 id참조)',
  `musicId` int(11) NOT NULL COMMENT '플리에 담긴 곡 id(product테이블 peodNum참조) ',
  `playDate` datetime NOT NULL COMMENT '재생일',
  PRIMARY KEY (`id`),
  KEY `userId` (`userId`),
  KEY `musicId` (`musicId`),
  CONSTRAINT `fk_playhistorys_users` FOREIGN KEY (`userId`) REFERENCES `users` (`id`) ON DELETE CASCADE ON UPDATE CASCADE,
  CONSTRAINT `fk_playhistorys_musics` FOREIGN KEY (`musicId`) REFERENCES `musics` (`id`) ON DELETE CASCADE ON UPDATE CASCADE
) ENGINE=InnoDB DEFAULT CHARSET=utf8;


